package january19;

public class SwitchEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day = 1;
		switch(day) {
		
		case 1:System.out.println("First day of week is sunday");
		break;
		
		case 2:System.out.println("Second day of week is Monday");
		break;
		
		case 3:System.out.println("Thrd day of week is tuesday");
		break;
		
		case 4:System.out.println("Fourth day of week is Wednesday");
		break;
		
		case 5:System.out.println("Fifth day of week is thursday");
		break;
		
		case 6:System.out.println("sixth day of week is friday");
		break;
		
		case 7:System.out.println("Seventh day of week is saturday");
		break;
		
		default:System.out.println("Invalid Condition");
		
			
			
		}

	}

}
